const markets = $json;
const rows = Array.isArray(markets) ? markets : markets.body || [];
function num(x){return (typeof x==='number')?x: (x?Number(x):0)}
const sorted24 = [...rows].sort((a,b)=>num(b.price_change_percentage_24h_in_currency)-num(a.price_change_percentage_24h_in_currency));
const gainers = sorted24.slice(0,5).map(x=>({symbol:String(x.symbol||'').toUpperCase(), name:x.name, price:x.current_price, ch24:num(x.price_change_percentage_24h_in_currency), ch7:num(x.price_change_percentage_7d_in_currency)}));
const losers = sorted24.slice(-5).reverse().map(x=>({symbol:String(x.symbol||'').toUpperCase(), name:x.name, price:x.current_price, ch24:num(x.price_change_percentage_24h_in_currency), ch7:num(x.price_change_percentage_7d_in_currency)}));
const get = s=>rows.find(x=>String(x.symbol||'').toLowerCase()===s)||{};
const btc = get('btc');
const eth = get('eth');
return [{ json: { gainers, losers, btc: {price:btc.current_price, ch24:num(btc.price_change_percentage_24h_in_currency), ch7:num(btc.price_change_percentage_7d_in_currency)}, eth: {price:eth.current_price, ch24:num(eth.price_change_percentage_24h_in_currency), ch7:num(eth.price_change_percentage_7d_in_currency)} } }];

const d = $json;
const md = (arr=[]) => arr.map(x=>`- **${x.symbol}** (${x.name}) — $${x.price} | 24h: ${Number(x.ch24||0).toFixed(2)}% | 7d: ${Number(x.ch7||0).toFixed(2)}%`).join('
');
const prompt = `You are a neutral crypto market analyst. Using the JSON data below, write a concise 120–160 word daily brief in Markdown. Mention BTC and ETH moves, notable top gainers/losers, and the Fear & Greed index with a one-line interpretation. Avoid advice language.

JSON:
${JSON.stringify(d)}`;
return [{ json: { prompt, table: `
**Top Gainers (24h)**
${md(d.gainers)}

**Top Losers (24h)**
${md(d.losers)}` } }];
